# include "convolution2.h"
# include<stdio.h>
# include<stdlib.h>

// current video shape
# define width 800  // test 100
# define height 600   // test 70

double Umap[256];
double UXmap[256];
int result[height][width];  // save multi-threshold image
int *pValueList, SIZE = 7;  // save L, LM, M, MH, H ... values and length

double handle(int low, int med, int high, int i){
	double curr_u;
	
	double left, right;
	double alpha, beta;	
	
	alpha = med - low;
	beta = high - med;
	
	left = (med >= i)?med - i:0;
	right = (i >= med)?i - med:0;
	
	curr_u = left / alpha + right / beta;
	curr_u = (curr_u <= 1)?1.0 - curr_u:0;
	
	return curr_u;
}

void U(int *ValueList, int size){
	int pos=0, i=0;
	int low, med, high;
	
	pValueList = ValueList;
	
	SIZE = size;  // the length of ValueList

	// create Umap & UXmap
	size = size / 2;	
    for (int index=0; index<size; index++){
    	pos = index * 2 + 1;
    	low = ValueList[pos - 1];
    	med = ValueList[pos];
    	high = ValueList[pos + 1];
    	
		while (i < high){
			Umap[i] = handle(low, med, high, i);
			UXmap[i] = Umap[i] * i;
			i++;
		}
	}
}

int SearchRange(int value){
	int interval=0;
	for (int i=2; i<=SIZE-1; i+=2){
		if (*(pValueList + i) < value)
			interval = i/2;
	}
	return interval;
}

void CMultiThreshold(int src[height][width]){
	int h, w;  // control loop
	int m, n;  // control matrix
	
	int val, index;
	int avg;
	
	int s = SIZE / 2;
	double *UValue, *UXValue;
	double *MValue, *diff;
	
	UValue = malloc(s * sizeof(double));
	UXValue = malloc(s * sizeof(double));
	MValue = malloc(s * sizeof(double));
	diff = malloc(s * sizeof(double));
	
	// initialize value 
	for (int init=0; init<s; init++){
		UValue[init] = 0;
		UXValue[init] = 0;
		MValue[init] = 0;
		diff[init] = 0;
	}
	// initialize value End.
	
	// control loop
	for (h=1; h<height-1; h++){
		for (w=1; w<width-1; w++){
			// control matrix
			for (m=h-1; m<=h+1; m++){
				for (n=w-1; n<=w+1; n++){
					// calculation
					val = src[m][n];
					index = SearchRange(val);  // return interval
					// printf("val: %d, index: %d\n", val, index);					
					*(UValue + index) += Umap[val];
					*(UXValue + index) += UXmap[val];
					avg += val;
					
//					if (h == 1 && w == 34){
//						printf("h: %d, w: %d;\n", h, w);
//						printf("m: %d, n: %d;\n", m, n);
//						printf("val=%d, index=%d\n", val, index);
//						printf("Umap:%.4f, UXmap:%.4f\n", Umap[val], UXmap[val]);
//						printf("UValue: %.4f, UXValue: %.4f\n", UValue[0], UXValue[0]);
//					}
					// calculation End.
				}					
			}
			// control matrix End.
			
			// check UValue & UXValue
//			for (int check=0;check<s;check++){
//				if (h == 1 && w == 34)
//					printf("U[%d] = %.2f, UX[%d] = %.2f\n", check, UValue[check], check, UXValue[check]);
//			}
			// check UValue & UXValue End. 
	
			// calculation avg, MValue and Result
			if (avg){
				avg = avg / 9;
				for (int i=0; i<s; i++){
					if (UValue[i])
						MValue[i] = UXValue[i] / UValue[i];
					else
						MValue[i] = 0;
					
					diff[i] = (MValue[i] > avg)?MValue[i] - avg:avg - MValue[i];
				}
				
				// check UValue & UXValue
//				for (int check=0;check<s;check++){
//					printf("M[%d] = %.2f, diff[%d] = %.2f\n", check, MValue[check], check, diff[check]);
//				}
			    // check UValue & UXValue End. 
			    
				// calculation gray value
				int regIndex = 0, regMinValue = 256;
				for (int i=0; i<s; i++){
					if (diff[i] < regMinValue){
						regMinValue = diff[i];
						regIndex = i;
					}
				}
				result[h][w] = 256 * regIndex / s;
			}
			else{
				result[h][w] = 0;	
			}
			// calculation avg, MValue and Result End.
			
			// clear the value of each pointer
			for (int clear=0; clear<s; clear++){
				UValue[clear] = 0;
				UXValue[clear] = 0;
				MValue[clear] = 0;
				diff[clear] = 0;
			}
			// clear the value of each pointer End.
		}
	}
	free(UValue); free(UXValue); free(MValue); free(diff);
	// control loop End.
}


